drop database if exists `project_dan_dung`;
create database if not exists `project_dan_dung`;
use `project_dan_dung`;

create table if not exists `user_account`
(
    `user_id`  int(11)      not null auto_increment primary key,
    `email`    varchar(255) not null,
    password   varchar(255) not null,
    full_name  varchar(255),
    phone      varchar(255),
    address    varchar(255),
    role       enum ('ADMIN', 'USER')      default 'USER',
    created_at timestamp                   default current_timestamp,
    updated_at timestamp                   default current_timestamp on update current_timestamp,
    status     enum ('ACTIVE', 'INACTIVE') default 'ACTIVE'
);

create table if not exists `category`
(
    `category_id` int(11)      not null auto_increment primary key,
    `name`        varchar(255) not null,
    `status`      enum ('ACTIVE', 'INACTIVE') default 'ACTIVE',
    `created_at`  timestamp                   default current_timestamp,
    `updated_at`  timestamp                   default current_timestamp on update current_timestamp
);


create table if not exists `product`
(
    `product_id`  int(11)      not null auto_increment primary key,
    `category_id` int(11)      not null,
    `name`        varchar(255) not null,
    `price`       decimal(25, 2)              default 0,
    `description` text,
    `status`      enum ('ACTIVE', 'INACTIVE') default 'ACTIVE',
    `url`         longtext,
    `created_at`  timestamp                   default current_timestamp,
    `updated_at`  timestamp                   default current_timestamp on update current_timestamp,
    foreign key (category_id) references category(category_id)
);


create table if not exists `cart`
(
    `cart_id`    int(11) not null auto_increment primary key,
    `user_id`    int(11) not null,
    `quantity`   int(11) not null,
    `total`      decimal(25, 2) default 0,
    `created_at` timestamp      default current_timestamp,
    `updated_at` timestamp      default current_timestamp on update current_timestamp,
    foreign key (user_id) references user_account(user_id)
);

create table if not exists `cart_detail`
(
    `cart_detail_id` int(11) not null auto_increment primary key,
    `cart_id`        int(11) not null,
    `product_id`     int(11) not null,
    `quantity`       int(11) not null,
    `price`          decimal(25, 2) default 0,
    `total`          decimal(25, 2) default 0,
    `created_at`     timestamp      default current_timestamp,
    `updated_at`     timestamp      default current_timestamp on update current_timestamp,
    foreign key (cart_id) references cart(cart_id),
    foreign key (product_id) references product(product_id)
);

create table if not exists `order`
(
    `order_id`   int(11) not null auto_increment primary key,
    `user_id`    int(11) not null,
    `total`      decimal(25, 2)                        default 0,
    `total_quantity` int(11)                           default 0,
    `address`    varchar(2000),
    `full_name` varchar(255),
    `email`     varchar(255),
    `phone`     varchar(255),
    `txn_ref`   varchar(255),
    `payment`    enum ('COD', 'VNPAY')                default 'COD',
    `status`     enum ('PENDING', 'SHIPPING', 'CANCEL', 'DONE') default 'PENDING',
    `created_at` timestamp                             default current_timestamp,
    `updated_at` timestamp                             default current_timestamp on update current_timestamp,
    foreign key (user_id) references user_account(user_id)
);

create table if not exists `order_detail`
(
    `order_detail_id` int(11) not null auto_increment primary key,
    `order_id`        int(11) not null,
    `product_id`      int(11) not null,
    `quantity`        int(11) not null,
    `price`           decimal(25, 2) default 0,
    `total`           decimal(25, 2) default 0,
    `created_at`      timestamp      default current_timestamp,
    `updated_at`      timestamp      default current_timestamp on update current_timestamp,
    foreign key (product_id) references product(product_id),
    foreign key (order_id) references `order`(order_id)
);



